import sys

def solve():
    n = int(input())
    a = list(map(int, input().split()))

    max_len = 1
    for i in range(1, n):
        if a[i] > a[i - 1]:
            max_len += 1
        else:
            if i < n - 1 and a[i] > a[i + 1]:
                max_len = max(max_len, 2)
            elif i == n - 1:
                max_len = max(max_len, 2)
            else:
                max_len = max(max_len, i)

    max_len = max(max_len, n - 1)
    for i in range(n - 2, -1, -1):
        if a[i] < a[i + 1]:
            max_len = max(max_len, n - i)

    return str(max_len)


if __name__ == "__main__":
    print(solve())
