import math

def gcd(a, b):
    """Compute the greatest common divisor of a and b"""
    while b:
        a, b = b, a % b
    return a

def lcm(a, b):
    """Compute the lowest common multiple of a and b"""
    return a * b // gcd(a, b)

def find_k(a, b):
    """Find the smallest non-negative integer k such that the LCM of a+k and b+k is the smallest possible"""
    k = 0
    while True:
        lcm_ab = lcm(a + k, b + k)
        if lcm(a, b) == lcm_ab or (lcm(a, b) % lcm_ab == 0 and lcm_ab < lcm(a + k + lcm(a, b) // lcm_ab, b + k + lcm(a, b) // lcm_ab)):
            break
        k += 1
    return k

# Read input from standard input
a, b = map(int, input().split())

# Print the result to standard output
print(find_k(a, b))
