import sys

def convert_to_decimal(number, base):
    """Convert a number from any base to decimal."""
    result = 0
    power = 0
    for digit in reversed(number):
        if digit.isdigit():
            result += int(digit) * (base ** power)
        else:
            # Convert Roman numeral digit to decimal
            roman_map = {'I': 1, 'V': 5, 'X': 10, 'L': 50, 'C': 100, 'D': 500, 'M': 1000}
            if digit.upper() == 'A':
                result += 10 * (base ** power)
            elif digit.upper() == 'B':
                result += 11 * (base ** power)
            else:
                result += roman_map[digit.upper()] * (base ** power)
        power += 1
    return result

def convert_from_decimal(number, base):
    """Convert a decimal number to any base."""
    if base < 2 or base > 36:
        raise ValueError("Base must be between 2 and 36")

    digits = "0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ"
    result = ""

    while number > 0:
        remainder = number % base
        result = digits[remainder] + result
        number //= base

    return result

