import sys

def read_input():
    n, x = map(int, input().split())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))
    return n, x, a, b

def find_worst_place(a, b, x):
    sorted_a = sorted(range(len(a)), key=lambda i: a[i])
    sorted_b = sorted(range(len(b)), key=lambda i: b[i], reverse=True)
    worst = 1
    for i in range(len(sorted_a)):
        if a[sorted_a[i]] + b[sorted_b[i]] >= x:
            worst += 1
    return worst

def find_best_place(a, b, x):
    sorted_a = sorted(range(len(a)), key=lambda i: a[i])
    sorted_b = sorted(range(len(b)), key=lambda i: b[i])
    best = 1
    for i in range(len(sorted_a)):
        if a[sorted_a[i]] + b[sorted_b[i]] >= x:
            best += 1
    return best

def main():
    n, x, a, b = read_input()
    worst = find_worst_place(a, b, x)
    best = find_best_place(a, b, x)
    print(f"{best} {worst}")

if __name__ == "__main__":
    main()
