import sys

def read_input():
    n, m = map(int, input().split())
    graph = [[] for _ in range(n + 1)]
    for _ in range(m):
        u, v = map(int, input().split())
        graph[u].append(v)
        graph[v].append(u)
    return n, graph

def dfs(node, parent, visited, component):
    visited[node] = True
    component.append(node)
    for neighbor in graph[node]:
        if not visited[neighbor]:
            dfs(neighbor, node, visited, component)

def find_components():
    visited = [False] * (n + 1)
    components = []
    for i in range(1, n + 1):
        if not visited[i]:
            component = []
            dfs(i, -1, visited, component)
            components.append(component)
    return components

def is_connected(component):
    return len(component) % 2 == 0

def find_odd_cycle(components):
    min_edges = float('inf')
    ways = 0
    for component in components:
        if is_connected(component):
            continue
        edges = len(component) - 1
        if edges < min_edges:
            min_edges = edges
            ways = 1
        elif edges == min_edges:
            ways += 1
    return min_edges, ways

