MOD = 998244353

def solve(N, K):
    dp = [[0] * (K + 1) for _ in range(N + 1)]
    dp[0][0] = 1

    for i in range(1, N + 1):
        for j in range(min(i, K) + 1):
            for k in range(j + 1):
                dp[i][j] += dp[i - 1][j - k]
                dp[i][j] %= MOD

    ans = 0
    for i in range(K + 1):
        ans += pow(2, i, MOD) * dp[N][i]
        ans %= MOD

    return ans

N, K = map(int, input().split())
print(solve(N, K))
