import sys

def solve():
    n = int(input())
    k = 2
    while k <= 10**6:
        if n == 1:
            return "NO"
        x = k + 1
        y = k + 1
        t = 1
        while True:
            t += 1
            if x > n or y > n:
                break
            x += (k - 1) * t
            y += (k - 1) * t
        if x == n or y == n:
            return "YES"
        k += 1
    return "NO"

t = int(input().strip())
for _ in range(t):
    print(solve())
