import re
from collections import deque

def solve():
    line = input()
    names = eval(line)
    results = []

    for name in names:
        parts = name.split(':-')
        jaw_str = parts[0]
        stomach_str = parts[1]

        num_vars = jaw_str.count('_')
        jaw_expr = jaw_str[2:-1].replace('_', 'x')

        constraints = []
        if stomach_str:
            cond_parts = stomach_str.split(',')
            for cond in cond_parts:
                cond = cond.strip()
                if not cond:
                    continue

                parts = re.split(r'(>|<|=)', cond)
                if len(parts) == 3:
                    lhs_str = parts[0].strip()
                    op = parts[1].strip()
                    rhs_str = parts[2].strip()

                    lhs_count = lhs_str.count('_')
                    rhs_count = rhs_str.count('_')
                    constraints.append((lhs_count, op, rhs_count))
                else:
                    # This case should not happen based on problem description and examples
                    pass

        found_solution = False
        min_solution = None

        q = deque([([], 0)]) # (current_values, next_var_index)

        while q:
            current_values, var_idx = q.popleft()

            if var_idx == num_vars:
                # All variables assigned, check constraints

                # First, check jaw_expr evaluation
                expr_with_values = jaw_expr
                for i in range(num_vars):
                    expr_with_values = expr_with_values.replace(f'x', str(current_values[i]), 1)

                try:
                    jaw_result = eval(expr_with_values)
                except (SyntaxError, TypeError, ZeroDivisionError):
                    continue # Invalid expression or division by zero

                if jaw_result != 0:
                    continue # Jaw expression must evaluate to 0

                # Then, check stomach constraints
                valid_constraints = True
                for lhs_count, op, rhs_count in constraints:
                    lhs_val = sum(current_values[:lhs_count])
                    rhs_val = sum(current_values[:rhs_count])

                    if op == '>':
                        if not (lhs_val > rhs_val):
                            valid_constraints = False
                            break
                    elif op == '<':
                        if not (lhs_val < rhs_val):
                            valid_constraints = False
                            break
                    elif op == '=': # Example only shows > and <, but good to be robust
                        if not (lhs_val == rhs_val):
                            valid_constraints = False
                            break

                if valid_constraints:
                    current_solution = "".join(map(str, current_values))
                    if not found_solution or current_solution < min_solution:
                        min_solution = current_solution
                        found_solution = True
                continue

            # Assign next variable
            for digit in range(10):
                new_values = current_values + [digit]
                q.append((new_values, var_idx + 1))

        if found_solution:
            results.append(min_solution)
        else:
            results.append("false")

    print(results)

