def solve():
    n, m = map(int, input().split())
    grades = []
    for _ in range(n):
        grades.append(input())

    successful_students = [False] * n

    for j in range(m):  # Iterate through subjects
        max_mark_in_subject = 0
        for i in range(n):  # Find the maximum mark for the current subject
            mark = int(grades[i][j])
            if mark > max_mark_in_subject:
                max_mark_in_subject = mark

        for i in range(n):  # Check which students are the best at this subject
            mark = int(grades[i][j])
            if mark == max_mark_in_subject:
                successful_students[i] = True

    count = 0
    for is_successful in successful_students:
        if is_successful:
            count += 1

    print(count)

if __name__ == '__main__':
    solve()
