def solve():
    tiles_str = input().split()
    tiles = []
    for t_str in tiles_str:
        num = int(t_str[0])
        suit = t_str[1]
        tiles.append((num, suit))

    def is_mentsu(hand):
        # Check for koutsu
        from collections import Counter
        counts = Counter(hand)
        for tile, count in counts.items():
            if count >= 3:
                return True

        # Check for shuntsu
        # Group tiles by suit
        tiles_by_suit = {'m': [], 'p': [], 's': []}
        for num, suit in hand:
            tiles_by_suit[suit].append(num)

        for suit in tiles_by_suit:
            nums = sorted(list(set(tiles_by_suit[suit]))) # Use set to handle duplicates for shuntsu
            if len(nums) < 3:
                continue
            for i in range(len(nums) - 2):
                if nums[i+1] == nums[i] + 1 and nums[i+2] == nums[i] + 2:
                    return True
        return False

    if is_mentsu(tiles):
        print(0)
        return

    # Try drawing one tile
    # Iterate through all possible tiles (1m-9m, 1p-9p, 1s-9s)
    possible_tiles = []
    for suit_char in ['m', 'p', 's']:
        for num_val in range(1, 10):
            possible_tiles.append((num_val, suit_char))

    for drawn_tile in possible_tiles:
        new_hand = tiles + [drawn_tile]
        if is_mentsu(new_hand):
            print(1)
            return

    # If 0 or 1 tile doesn't work, it must be 2 tiles.
    # The problem constraints and typical competitive programming problem design
    # imply that 0, 1, or 2 tiles will always be sufficient.
    # We don't need to explicitly check for 2 tiles.
    # A hand of 3 tiles, even if completely unrelated, can always form a mentsu
    # by drawing two specific tiles.
    # Example: [1m, 5p, 9s]
    # Draw 1m, 1m -> [1m, 1m, 1m, 5p, 9s] -> koutsu
    # Draw 2m, 3m -> [1m, 2m, 3m, 5p, 9s] -> shuntsu
    # So, 2 tiles are always sufficient.
    print(2)

