def solve():
    tiles_str = input().split()
    tiles = []
    for t_str in tiles_str:
        tiles.append((int(t_str[0]), t_str[1]))

    def is_mentsu(current_tiles):
        n = len(current_tiles)
        if n < 3:
            return False

        # Check for koutsu (triplet)
        from collections import Counter
        counts = Counter(current_tiles)
        for tile, count in counts.items():
            if count >= 3:
                return True

        # Check for shuntsu (sequence)
        # Group tiles by suit
        tiles_by_suit = {'m': [], 'p': [], 's': []}
        for num, suit in current_tiles:
            tiles_by_suit[suit].append(num)

        for suit in tiles_by_suit:
            nums = sorted(list(set(tiles_by_suit[suit]))) # Use set to handle duplicates for sequence checking
            for i in range(len(nums)):
                if nums[i] <= 7: # A sequence must start with a number <= 7
                    if nums[i] + 1 in nums and nums[i] + 2 in nums:
                        return True
        return False

    if is_mentsu(tiles):
        print(0)
        return

    # Try drawing 1 tile
    # Iterate through all possible tiles to draw
    # 1m-9m, 1p-9p, 1s-9s
    possible_new_tiles = []
    for suit in ['m', 'p', 's']:
        for num in range(1, 10):
            possible_new_tiles.append((num, suit))

    for new_tile in possible_new_tiles:
        temp_tiles = tiles + [new_tile]
        if is_mentsu(temp_tiles):
            print(1)
            return

    # If 0 or 1 tile doesn't work, then 2 tiles are needed.
    # The problem statement implies that 0, 1 or 2 draws are sufficient
    # for 3 initial tiles.
    # A simple proof:
    # If we have 3 distinct tiles (e.g., 1m, 3p, 5s), we can draw two more
    # to form a mentsu. For example, draw 1m, 1m to make [1m, 1m, 1m, 3p, 5s].
    # Or draw 2m, 3m to make [1m, 2m, 3m, 3p, 5s].
    # So, 2 draws are always sufficient if 0 or 1 are not.
    print(2)

