import sys
import re
from itertools import permutations

notes_order = ["C","C#","D","D#","E","F","F#","G","G#","A","B","H"]
note_to_idx = {n:i for i,n in enumerate(notes_order)}

data = sys.stdin.read().strip()

if not data:
    sys.exit(0)

# Try to extract chords given in single-quoted list like ['C E G', 'C# B F']
quoted = re.findall(r"'([^']+)'", data)
if quoted:
    chords = [q.strip() for q in quoted if q.strip()]
else:
    # Otherwise, try lines. Each non-empty line is a chord or maybe whole input is one chord.
    lines = [line.strip() for line in data.splitlines() if line.strip()]
    if len(lines) == 1:
        # maybe the single line contains exactly three notes separated by spaces
        parts = lines[0].split()
        if len(parts) == 3:
            chords = [' '.join(parts)]
        else:
            # fallback: split by commas into items, strip quotes and spaces
            items = [itm.strip().strip('\'"') for itm in re.split(r',', lines[0]) if itm.strip()]
            # choose items that have at least one space (likely chords)
            chords = [it for it in items if ' ' in it]
            if not chords:
                # as last resort, if items length 3 and each is a note, combine into one chord
                if len(items) == 3 and all(it in note_to_idx for it in items):
                    chords = [' '.join(items)]
                else:
                    # take whole line as chord
                    chords = [' '.join(parts)]
    else:
        # multiple lines: each line should be a chord
        chords = []
        for line in lines:
            parts = line.split()
            if len(parts) == 3:
                chords.append(' '.join(parts))
            else:
                # try to extract three-note substring
                m = re.search(r'([A-G](?:#)?\s+[A-G](?:#)?\s+[A-G](?:#)?)', line)
                if m:
                    chords.append(m.group(1))
                else:
                    # strip commas/quotes and use as is
                    chords.append(line.strip().strip('\'"'))

def classify(chord):
    toks = chord.split()
    if len(toks) != 3:
        return "strange"
    try:
        idxs = [note_to_idx[t] for t in toks]
    except KeyError:
        return "strange"
    for perm in permutations(idxs, 3):
        a,b,c = perm
        d1 = (b - a) % 12
        d2 = (c - b) % 12
        if d1 == 4 and d2 == 3:
            return "major"
        if d1 == 3 and d2 == 4:
            return "minor"
    return "strange"

results = [classify(ch) for ch in chords]

# Output
if len(results) == 1:
    print(results[0])
else:
    for r in results:
        print(r)

