data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)

def classify(line):
    parts = line.strip().split()
    if len(parts) != 3:
        return "strange"
    a,b,c = parts
    if a not in idx or b not in idx or c not in idx:
        return "strange"
    arr = [a,b,c]
    # try all orderings as X Y Z around scale (ascending distances from X)
    for perm in [(0,1,2),(0,2,1),(1,0,2),(1,2,0),(2,0,1),(2,1,0)]:
        x = arr[perm[0]]; y = arr[perm[1]]; z = arr[perm[2]]
        ix = idx[x]; iy = idx[y]; iz = idx[z]
        dxy = (iy - ix) % 12
        dyz = (iz - iy) % 12
        if dxy == 4 and dyz == 3:
            return "major"
        if dxy == 3 and dyz == 4:
            return "minor"
    return "strange"

