cand_strings = [s for s in arr if len(s) == n - 1]
a, b = cand_strings[0], cand_strings[1]

def try_candidate(candidate):
    # compute needed counts for prefixes and suffixes
    needP = {}
    needS = {}
    for L in range(1, n):
        pre = candidate[:L]
        suf = candidate[-L:]
        needP[pre] = needP.get(pre, 0) + 1
        needS[suf] = needS.get(suf, 0) + 1
    total = {}
    for s in arr:
        total[s] = total.get(s, 0) + 1
    # validate counts possible
    for s, cnt in total.items():
        p = needP.get(s, 0)
        sfx = needS.get(s, 0)
        if p + sfx != cnt:
            return None
    usedP = {}
    usedS = {}
    res = []
    for s in arr:
        if usedP.get(s, 0) < needP.get(s, 0):
            res.append('P')
            usedP[s] = usedP.get(s, 0) + 1
        else:
            res.append('S')
            usedS[s] = usedS.get(s, 0) + 1
    return ''.join(res)

cand1 = a + b[-1]
cand2 = b + a[-1]

