import sys

def main():
    data = [line.rstrip('\n') for line in sys.stdin.readlines()]
    if not data:
        return
    idx = 0
    n = int(data[idx].strip()); idx += 1
    total = 2 * n - 2
    arr = []
    for _ in range(total):
        arr.append(data[idx].strip()); idx += 1

    # find the two strings of length n-1
    long_idxs = [i for i, s in enumerate(arr) if len(s) == n-1]
    a = arr[long_idxs[0]]
    b = arr[long_idxs[1]]

    candidates = [a + b[-1], b + a[-1]]

    for cand in candidates:
        desiredP = {}
        desiredS = {}
        ok = True
        for l in range(1, n):
            p = cand[:l]
            sfx = cand[n-l:]
            desiredP[p] = desiredP.get(p, 0) + 1
            desiredS[sfx] = desiredS.get(sfx, 0) + 1

        res = [''] * total
        dp = desiredP.copy()
        ds = desiredS.copy()
        for i, t in enumerate(arr):
            if dp.get(t, 0) > 0:
                res[i] = 'P'
                dp[t] -= 1
            elif ds.get(t, 0) > 0:
                res[i] = 'S'
                ds[t] -= 1
            else:
                ok = False
                break
        if ok:
            sys.stdout.write(''.join(res))
            return

if __name__ == "__main__":
    main()

