import sys

data = sys.stdin.read().split()
if not data:
    sys.exit(0)
n = int(data[0])
m = 2 * n - 2
arr = data[1:1 + m]

by_len = {}
for i, s in enumerate(arr):
    by_len.setdefault(len(s), []).append(i)

i1, i2 = by_len[n - 1][0], by_len[n - 1][1]
a, b = arr[i1], arr[i2]
candidates = [a + b[-1], b + a[-1]]

def try_candidate(s):
    ans = [''] * m
    for k in range(1, n):
        p = s[:k]
        suf = s[n - k:]
        idxs = by_len[k]
        iA, iB = idxs[0], idxs[1]
        sA, sB = arr[iA], arr[iB]
        if p == suf:
            if sA != p or sB != p:
                return None
            ans[iA] = 'P'
            ans[iB] = 'S'
        else:
            if sA == p and sB == suf:
                ans[iA] = 'P'
                ans[iB] = 'S'
            elif sB == p and sA == suf:
                ans[iB] = 'P'
                ans[iA] = 'S'
            else:
                return None
    return ''.join(ans)

for cand in candidates:
    res = try_candidate(cand)
    if res is not None:
        sys.stdout.write(res)
        break

