import sys

def main():
    data = list(map(int, sys.stdin.buffer.read().split()))
    it = iter(data)
    try:
        n = next(it)
    except StopIteration:
        return
    k = next(it)
    adj = [[] for _ in range(n+1)]
    for _ in range(n-1):
        a = next(it); b = next(it)
        adj[a].append(b)
        adj[b].append(a)

    root = n
    parent = [-1] * (n+1)
    parent[root] = 0
    stack = [root]
    while stack:
        v = stack.pop()
        for u in adj[v]:
            if parent[u] == -1:
                parent[u] = v
                stack.append(u)

    to_select = n - k
    used = [False] * (n+1)
    selected = 0
    for v in range(n, 0, -1):
        if selected >= to_select:
            break
        if used[v]:
            continue
        path = []
        u = v
        while u and not used[u]:
            path.append(u)
            u = parent[u]
        if selected + len(path) <= to_select:
            for x in path:
                used[x] = True
            selected += len(path)

    removed = [str(i) for i in range(1, n+1) if not used[i]]
    print(" ".join(removed))

if __name__ == "__main__":
    main()
