    total = 0
    for j in range(k):
        W = w - 4*j
        H = h - 4*j
        if W <= 0 or H <= 0:
            break
        if W >= 2 and H >= 2:
            border = W*H - (W-2)*(H-2)
        else:
            border = W*H
        total += border
    outs.append(str(total))

sys.stdout.write("\n".join(outs))

