import sys

def solve_triplet(tokens):
    tiles = tokens
    nums = []
    suits = []
    for t in tiles:
        if len(t) < 2:
            return 2
        nums.append(int(t[0]))
        suits.append(t[1])
    # check 0 (already mentsu)
    # koutsu
    if tiles[0] == tiles[1] == tiles[2]:
        return 0
    # shuntsu
    # need same suit and numbers consecutive
    if suits[0] == suits[1] == suits[2]:
        s_nums = sorted(nums)
        if s_nums[1] == s_nums[0] + 1 and s_nums[2] == s_nums[1] + 1:
            return 0
    # check if one tile away
    # any pair equal -> need one same tile
    for i in range(3):
        for j in range(i+1,3):
            if tiles[i] == tiles[j]:
                return 1
            if suits[i] == suits[j]:
                d = abs(nums[i] - nums[j])
                if d <= 2:
                    return 1
    return 2

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    out_lines = []
    # assume inputs come in groups of 3
    n = len(data) // 3
    for i in range(n):
        group = data[3*i:3*i+3]
        out_lines.append(str(solve_triplet(group)))
    sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
    main()

