        tiles.append(tok[:2])
# If more than 3 tokens provided (multiple test cases), process in groups of 3
outs = []
i = 0
while i + 2 < len(tiles):
    hand = tiles[i:i+3]
    i += 3
    # if already has mentsu -> 0
    def is_triplet(h):
        return h[0]==h[1]==h[2]
    def is_sequence(h):
        s = [t for t in h]
        suits = [x[1] for x in s]
        if suits[0]==suits[1]==suits[2]:
            nums = sorted(int(x[0]) for x in s)
            return nums[0]+1==nums[1] and nums[1]+1==nums[2]
        return False
    if is_triplet(hand) or is_sequence(hand):
        outs.append('0')
        continue
    # check if one tile needed -> if any pair can be extended to triplet or sequence
    need1 = False
    for a in range(3):
        for b in range(a+1,3):
            x = hand[a]; y = hand[b]
            # triplet possibility: same tile
            if x == y:
                need1 = True
            # sequence possibility: same suit and numbers within 2
            if x[1] == y[1]:
                nx = int(x[0]); ny = int(y[0])
                d = abs(nx-ny)
                if d <= 2:
                    need1 = True
    if need1:
        outs.append('1')
    else:
        outs.append('2')

sys.stdout.write("\n".join(outs))

