        a[i] = next(it)
    pref1 = [0] * (n + 1)
    pref2 = [0] * (n + 1)
    for i in range(1, n + 1):
        pref1[i] = pref1[i-1] + (1 if a[i] == 1 else 0)
        pref2[i] = pref2[i-1] + (1 if a[i] == 2 else 0)
    D = [0] * (n + 1)
    for x in range(0, n + 1):
        D[x] = pref2[x] - pref1[x]
    total_twos = pref2[n]
    ans = 0
    for l in range(1, n + 1):
        maxD = D[l-1]
        for r in range(l, n + 1):
            if D[r] > maxD:
                maxD = D[r]
            f = pref1[r] - pref2[l-1] + maxD
            total = pref1[l-1] + f + (total_twos - pref2[r])
            if total > ans:
                ans = total
    out_lines.append(str(ans))
sys.stdout.write("\n".join(out_lines))

