import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
idx = 0
out_lines = []
while idx < len(data):
    n = data[idx]; idx += 1
    a = data[idx:idx+n]; idx += n
    total_twos = sum(1 for x in a if x == 2)
    prefg = [0] * (n+1)
    for i in range(1, n+1):
        prefg[i] = prefg[i-1] + (1 if a[i-1] == 1 else -1)
    ans = total_twos + max(prefg)  # original best
    for l in range(1, n+1):
        min_pref = 10**9
        for r in range(l+1, n+1):
            if prefg[r-1] < min_pref:
                min_pref = prefg[r-1]
            cand = total_twos + prefg[r] + prefg[l-1] - min_pref
            if cand > ans:
                ans = cand
    if ans > n:
        ans = n
    out_lines.append(str(ans))
sys.stdout.write("\n".join(out_lines))

