import sys

MOD = 1000000007

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
n = int(data[0]); m = int(data[1]); g = int(data[2])

N = n + m
# factorials up to N (we may access up to N)
fact = [1] * (N + 1)
for i in range(1, N + 1):
    fact[i] = fact[i-1] * i % MOD
invfact = [1] * (N + 1)
invfact[N] = pow(fact[N], MOD-2, MOD)
for i in range(N, 0, -1):
    invfact[i-1] = invfact[i] * i % MOD

def comb(a, b):
    if b < 0 or a < 0 or b > a:
        return 0
    return fact[a] * invfact[b] % MOD * invfact[a-b] % MOD

ans = 0

# case b0 = 0 (first char of reversed string is 0) => requires n>=1
if n >= 1:
    if m == 0:
        if g == ((n-1) % 2):
            ans = (ans + 1) % MOD
    else:
        zprime = n - 1
        for k in range(0, zprime + 1):
            if k % 2 == g:
                a = n + m - k - 2
                b = zprime - k
                ans = (ans + comb(a, b)) % MOD

# case b0 = 1 => requires m>=1
if m >= 1:
    if m == 1:
        zprime = n
        final = 1 - (zprime % 2)
        if g == final:
            ans = (ans + 1) % MOD
    else:
        zprime = n
        for k in range(0, zprime + 1):
            if k % 2 == g:
                a = n + m - k - 2
                b = zprime - k
                ans = (ans + comb(a, b)) % MOD

print(ans % MOD)
