    row = rows[i]
    for j in range(n):
        if i == j or i + j == n - 1:
            if row[j] != diag_char:
                ok = False
                break
        else:
            if row[j] != other_char:
                ok = False
                break
    if not ok:
        break
print("YES" if ok else "NO")

