import sys

def main():
    data = list(map(int, sys.stdin.read().split()))
    if not data:
        return
    it = iter(data)
    n = next(it)
    a = [next(it) for _ in range(n)]
    a.sort()
    if n == 2:
        print(0)
    else:
        print(min(a[-2] - a[0], a[-1] - a[1]))

if __name__ == "__main__":
    main()
