import sys

def process(a, s):
    a_list = list(a.strip())
    s_sorted = sorted(s.strip(), reverse=True)
    j = 0
    for i in range(len(a_list)):
        if j < len(s_sorted) and s_sorted[j] > a_list[i]:
            a_list[i] = s_sorted[j]
            j += 1
        if j >= len(s_sorted):
            break
    return ''.join(a_list)

def main():
    data = [line.rstrip('\n') for line in sys.stdin if line.rstrip('\n') != '']
    out_lines = []
    i = 0
    while i + 1 < len(data):
        a = data[i].strip()
        s = data[i+1].strip()
        out_lines.append(process(a, s))
        i += 2
    if out_lines:
        sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
