import sys

def process_case(a, s):
    s_sorted = sorted(s, reverse=True)
    k = 0
    res = []
    for ch in a:
        if k < len(s_sorted) and s_sorted[k] > ch:
            res.append(s_sorted[k])
            k += 1
        else:
            res.append(ch)
    return ''.join(res)

def main():
    data = [line.strip() for line in sys.stdin.readlines() if line.strip() != '']
    out = []
    i = 0
    while i + 1 < len(data):
        a = data[i]
        s = data[i+1]
        out.append(process_case(a, s))
        i += 2
    # If there's only one line (shouldn't happen in valid input), do nothing.
    sys.stdout.write("\n".join(out))

