import sys

def process_pair(a, s):
    s_sorted = sorted(s, reverse=True)
    a_list = list(a)
    j = 0
    for i in range(len(a_list)):
        if j < len(s_sorted) and s_sorted[j] > a_list[i]:
            a_list[i] = s_sorted[j]
            j += 1
        if j >= len(s_sorted):
            break
    return ''.join(a_list)

def main():
