    outputs = []
    i = 0
    while i + 1 < len(data):
        a = data[i]
        s = data[i+1]
        outputs.append(process_pair(a, s))
        i += 2
    # If there's only one pair and input had exactly two lines, still works.
    # Print results, one per line
    if outputs:
        sys.stdout.write("\n".join(outputs))

