import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
N = int(data[0])

if N <= 2:
    print(-1)
    sys.exit(0)

# Fill column-wise with snake pattern: columns 0,2,4... top->down; columns 1,3,5... bottom->up
a = [[0]*N for _ in range(N)]
num = 1
for col in range(N):
    if col % 2 == 0:
        for row in range(N):
            a[row][col] = num
            num += 1
    else:
        for row in range(N-1, -1, -1):
            a[row][col] = num
            num += 1

out = []
for row in range(N):
    out.append(" ".join(str(x) for x in a[row]))
print("\n".join(out))

