if not data:
    sys.exit(0)

out_lines = []
for s in data:
    n = len(s)
    # must contain only a,b,c
    i = 0
    # count a's
    if i < n and s[i] == 'a':
        while i < n and s[i] == 'a':
            i += 1
    else:
        out_lines.append("NO")
        continue
    count_a = i

    # count b's
    if i < n and s[i] == 'b':
        start_b = i
        while i < n and s[i] == 'b':
            i += 1
        count_b = i - start_b
    else:
        out_lines.append("NO")
        continue

    # count c's
    if i < n and s[i] == 'c':
        start_c = i
        while i < n and s[i] == 'c':
            i += 1
        count_c = i - start_c
    else:
        out_lines.append("NO")
        continue

    # no other characters
    if i != n:
        out_lines.append("NO")
        continue

    # counts must be >=1 for a and b (already ensured), c must equal a or b
    if count_c == count_a or count_c == count_b:
        out_lines.append("YES")
    else:
        out_lines.append("NO")

sys.stdout.write("\n".join(out_lines))

