def check(s):
    n = len(s)
    i = 0
    ca = 0
    while i < n and s[i] == 'a':
        ca += 1
        i += 1
    if ca == 0:
        return "NO"
    cb = 0
    while i < n and s[i] == 'b':
        cb += 1
        i += 1
    if cb == 0:
        return "NO"
    cc = 0
    while i < n and s[i] == 'c':
        cc += 1
        i += 1
    if cc == 0:
        return "NO"
    if i != n:
        return "NO"
    return "YES" if (cc == ca or cc == cb) else "NO"

