import sys, re

data = sys.stdin.read()
tests = re.findall(r'[abc]+', data)
if not tests:
    sys.exit()

res = []
for s in tests:
    n = len(s)
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    if i == 0 or i == n or s[i] != 'b':
        res.append("NO"); continue
    j = i
    while j < n and s[j] == 'b':
        j += 1
    if j == i or j == n or s[j] != 'c':
        res.append("NO"); continue
    k = j
    while k < n and s[k] == 'c':
        k += 1
    if k != n:
        res.append("NO"); continue
    cnt_a = i
    cnt_b = j - i
    cnt_c = k - j
    if cnt_c == cnt_a or cnt_c == cnt_b:
        res.append("YES")
    else:
        res.append("NO")

print("\n".join(res))

