import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
n = data[0]
a = data[1:]
if n == 0:
    print(0)
    sys.exit(0)
if n == 1:
    print(1)
    sys.exit(0)

incL = [1]*n
for i in range(1, n):
    if a[i] > a[i-1]:
        incL[i] = incL[i-1] + 1
    else:
        incL[i] = 1

incR = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        incR[i] = incR[i+1] + 1
    else:
        incR[i] = 1

ans = max(incL)  # without any change

for i in range(n):
    if i == 0:
        ans = max(ans, 1 + incR[1])
    elif i == n-1:
        ans = max(ans, incL[n-2] + 1)
    else:
        # can we set a[i] to an integer x such that a[i-1] < x < a[i+1]?
        if a[i-1] + 1 < a[i+1]:
            ans = max(ans, incL[i-1] + 1 + incR[i+1])
        else:
            ans = max(ans, incL[i-1] + 1)
            ans = max(ans, incR[i+1] + 1)

# cap by n
if ans > n:
    ans = n
print(ans)

