import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [int(next(it)) for _ in range(n)]

if n == 1:
    print(1)
    sys.exit(0)

L = [1] * n
for i in range(1, n):
    if a[i] > a[i-1]:
        L[i] = L[i-1] + 1
    else:
        L[i] = 1

R = [1] * n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        R[i] = R[i+1] + 1
    else:
        R[i] = 1

ans = max(L)  # without any change inside subsegment
for i in range(n):
    if i == 0:
        ans = max(ans, 1 + R[1])
    elif i == n-1:
        ans = max(ans, L[n-2] + 1)
    else:
        # change a[i] to try to connect left and right
        if a[i+1] - a[i-1] >= 2:
            ans = max(ans, L[i-1] + 1 + R[i+1])
        # or just extend one side by changing an end element of subsegment
        ans = max(ans, L[i-1] + 1, R[i+1] + 1)

if ans > n:
    ans = n
print(ans)

