import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [0] * (n + 1)
for i in range(1, n + 1):
    a[i] = int(next(it))

L = [0] * (n + 2)
R = [0] * (n + 2)

for i in range(1, n + 1):
    if i == 1 or a[i] <= a[i-1]:
        L[i] = 1
    else:
        L[i] = L[i-1] + 1

for i in range(n, 0, -1):
    if i == n or a[i] >= a[i+1]:
        R[i] = 1
    else:
        R[i] = R[i+1] + 1

ans = 1
for i in range(1, n + 1):
    if L[i] > ans:
        ans = L[i]

for i in range(1, n + 1):
    left = L[i-1] if i-1 >= 1 else 0
    right = R[i+1] if i+1 <= n else 0
    # change a[i]
    if i > 1:
        ans = max(ans, left + 1)
    if i < n:
        ans = max(ans, right + 1)
    if 1 < i < n and a[i+1] - a[i-1] >= 2:
        ans = max(ans, left + 1 + right)

if ans > n:
    ans = n
print(ans)

