import sys

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit()
n = data[0]
a = data[1:1+n]

if n == 0:
    print(0)
    sys.exit()
if n == 1:
    print(1)
    sys.exit()

L = [1] * n
for i in range(1, n):
    if a[i] > a[i-1]:
        L[i] = L[i-1] + 1
    else:
        L[i] = 1

R = [1] * n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        R[i] = R[i+1] + 1
    else:
        R[i] = 1

ans = max(L)
for i in range(n):
    if i == 0:
        ans = max(ans, 1 + (R[1] if n > 1 else 0))
    elif i == n-1:
        ans = max(ans, L[n-2] + 1)
    else:
        if a[i+1] - a[i-1] > 1:
            ans = max(ans, L[i-1] + 1 + R[i+1])
        else:
            ans = max(ans, L[i-1] + 1, R[i+1] + 1)

if ans > n:
    ans = n
print(ans)

