import sys

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    a = [int(next(it)) for _ in range(n)]
    if n == 1:
        print(1)
        return

    l = [1] * n
    for i in range(1, n):
        if a[i] > a[i-1]:
            l[i] = l[i-1] + 1
        else:
            l[i] = 1

    r = [1] * n
    for i in range(n-2, -1, -1):
        if a[i] < a[i+1]:
            r[i] = r[i+1] + 1
        else:
            r[i] = 1

    ans = max(l)
    for i in range(n):
        if i > 0:
            ans = max(ans, l[i-1] + 1)
        if i < n-1:
            ans = max(ans, r[i+1] + 1)
        if 0 < i < n-1 and a[i+1] - a[i-1] >= 2:
            ans = max(ans, l[i-1] + 1 + r[i+1])

    if ans > n:
        ans = n
    print(ans)

if __name__ == "__main__":
    main()

