#!/usr/bin/env python3
import sys

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
n = data[0]
a = data[1:]
if n == 0:
    print(0)
    sys.exit(0)
if n == 1:
    print(1)
    sys.exit(0)

l = [1] * n
for i in range(1, n):
    if a[i] > a[i-1]:
        l[i] = l[i-1] + 1

r = [1] * n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1] + 1

ans = max(l)
for i in range(n):
    left = l[i-1] if i-1 >= 0 else 0
    right = r[i+1] if i+1 < n else 0
    if left + 1 > ans:
        ans = left + 1
    if right + 1 > ans:
        ans = right + 1
    if i-1 >= 0 and i+1 < n and a[i+1] - a[i-1] >= 2:
        val = left + 1 + right
        if val > ans:
            ans = val

if ans > n:
    ans = n
print(ans)

