import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [int(next(it)) for _ in range(n)]

if n == 1:
    print(1)
    sys.exit(0)

l = [1]*n
for i in range(1,n):
    if a[i] > a[i-1]:
        l[i] = l[i-1]+1

r = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1]+1

ans = max(l)  # at least the longest already increasing
for i in range(n):
    # try removing/changing a[i]
    left = l[i-1] if i-1 >= 0 else 0
    right = r[i+1] if i+1 < n else 0
    # join if possible
    if 0 <= i-1 and i+1 < n and a[i+1] - a[i-1] > 1:
        ans = max(ans, left + 1 + right)
    else:
        ans = max(ans, left + 1, 1 + right)
# cap at n
if ans > n:
    ans = n
print(ans)
