data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
pairs = []
if len(data) == 2:
    pairs = [tuple(data)]
else:
    it = iter(data)
    for x, y in zip(it, it):
        pairs.append((x, y))

out = []
for a, b in pairs:
    if a == b:
        out.append("0")
        continue
    if a > b:
        a, b = b, a
    diff = b - a
    best_k = 0
    best_lcm = (a * b) // math.gcd(a, b)
    i = 1
    while i * i <= diff:
        if diff % i == 0:
            for d in (i, diff // i):
                k = (d - a % d) % d
                A = a + k
                B = b + k
                g = math.gcd(A, B)
                l = (A // g) * B
                if l < best_lcm or (l == best_lcm and k < best_k):
                    best_lcm = l
                    best_k = k
        i += 1
    out.append(str(best_k))

sys.stdout.write("\n".join(out))

