import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

a = int(data[0])
b = data[1]
c = data[2]

# convert c (string in base a) to integer
def char_val(ch):
    if '0' <= ch <= '9':
        return ord(ch) - ord('0')
    else:
        return ord(ch) - ord('A') + 10

n = 0
for ch in c:
    n = n * a + char_val(ch)

# if target is not Roman
if b != 'R':
    b_int = int(b)
    if n == 0:
        print('0')
        sys.exit(0)
    digits = []
    while n > 0:
        d = n % b_int
        if d < 10:
            digits.append(chr(ord('0') + d))
        else:
            digits.append(chr(ord('A') + (d - 10)))
        n //= b_int
    print(''.join(reversed(digits)))
    sys.exit(0)

# convert integer n to Roman numeral
# n guaranteed >0 in Roman case
res = []

# symbols per power: tuples of (one, five, ten)
symbols = [
    ('I','V','X'),
    ('X','L','C'),
    ('C','D','M'),
    # for power >=3, only 'one'='M', five and ten absent
]

s = str(n)
L = len(s)
for i, ch in enumerate(s):
    digit = ord(ch) - ord('0')
    power = L - i - 1
    if power >= 3:
        # just repeat 'M' digit times times 10^(power-3)
        # Actually for each extra power above thousand, the digit represents that many thousands times 10^(power-3)
        # But roman for thousands place: repeat 'M' digit times for that decimal digit (e.g., 3000 -> 'MMM')
        # For larger powers >=4 where five/ten undefined, we treat each decimal digit as that many repetitions of 'M' times 10^(power-3)
        # This can be obtained by appending digit * (10**(power-3)) times 'M', but that's huge. However problem constraints ensure n manageable.
        # Simpler: for each thousand in that digit position we need to append digit * (10**(power-3)) times 'M'.
        count_M = digit * (10 ** (power - 3))
        res.append('M' * count_M)
    else:
        one, five, ten = symbols[power]
        if digit <= 3:
            res.append(one * digit)
        elif digit == 4:
            if five:
                res.append(one + five)
            else:
                res.append(one * 4)
        elif 5 <= digit <= 8:
            if five:
                res.append(five + one * (digit - 5))
            else:
                res.append(one * digit)
        elif digit == 9:
            if ten:
                res.append(one + ten)
            else:
                res.append(one * 9)

print(''.join(res))

