    hundreds = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM"]
    thousands = "M" * (v // 1000)
    rest = v % 1000
    res = thousands + hundreds[rest // 100] + tens[(rest % 100) // 10] + units[rest % 10]
    print(res)
else:
    b = int(b_tok)
    if v == 0:
        print("0")
    else:
        digits = []
        while v > 0:
            d = v % b
