        l_p = fl.get(p, 0)
        if g_p == l_p:
            # both must be that exponent
            if u in fixed[p]:
                if fixed[p][u] != g_p:
                    print("NO"); sys.exit(0)
            fixed[p][u] = g_p
            if v in fixed[p]:
