                    if p in fixed and v in fixed[p]:
                        if fixed[p][v] != exp[v]:
                            print("NO"); sys.exit(0)
                    dq.append(v)
    exp_per_prime[p] = exp

# Build final ai
ai = [1]*n
for p, mp in exp_per_prime.items():
    for node, e in mp.items():
        if e > 0:
            # multiply p**e
            ai[node] *= pow(p, e)

