    for _ in range(m):
        u = int(next(it)) - 1
        v = int(next(it)) - 1
        g = int(next(it))
        l = int(next(it))
        edges.append((u, v, g, l))
        if g > maxnum: maxnum = g
        if l > maxnum: maxnum = l

    MAXV = max(2, maxnum)
    spf = sieve_spf(MAXV)

