            print("NO")
            return
        fg = factorize(g, spf) if g > 1 else {}
        fl = factorize(l, spf) if l > 1 else {}
        # For each prime in l, get exponents
        for p, el in fl.items():
            eg = fg.get(p, 0)
            # eg must be <= el by divisibility
            if eg > el:
                print("NO")
                return
            prime_edges[p].append((u, v, eg, el))
        # Also ensure g has no primes outside l (shouldn't happen since l%g==0)
        for p in fg.keys():
            if p not in fl:
                print("NO")
                return

    # For each prime, solve assignments of exponents per node
    # store exponents per node in dict of dicts
    node_prime_exp = [dict() for _ in range(n)]  # list of dict p->exp

