                comp.append(cur)
                for (v, a, b) in adj[cur]:
                    if v not in visited:
                        visited.add(v)
                        dq.append(v)
            # try to assign this component
            assigned_comp = None
            # pick a seed node
            seed = comp[0]
            seed_vals = list(allowed[seed])
