                        if xv != a and xv != b:
                            ok = False
                            break
                        expected = b if xv == a else a
                        # expected must be in allowed[y]
                        if expected not in allowed[y]:
                            ok = False
                            break
                        if y in assign:
                            if assign[y] != expected:
                                ok = False
                                break
                        else:
                            assign[y] = expected
                            q.append(y)
                if ok:
                    # verify all component nodes assigned (they should be)
                    if len(assign) != len(comp):
                        # some nodes not reachable? shouldn't happen
                        ok = False
                    else:
                        assigned_comp = assign
                        success = True
                        break
            if not success:
                print("NO")
                return
            # commit assignments
            for node, val in assigned_comp.items():
                node_prime_exp[node][p] = val

    # Build final numbers
    res = [1] * n
    for i in range(n):
        prod = 1
        for p, e in node_prime_exp[i].items():
            prod *= pow(p, e)
        res[i] = prod

    # Validate all edges constraints
    import math
    for (u, v, g, l) in edges:
        a = res[u]; b = res[v]
        gg = math.gcd(a, b)
        if gg != g:
            print("NO")
            return
        ll = (a // gg) * b  # lcm
        if ll != l:
            print("NO")
            return

    print("YES")
    print(" ".join(str(x) for x in res))

