import sys, ast

data = sys.stdin.read()
if not data.strip():
    sys.exit()
s_list = None
txt = data.strip()
try:
    parsed = ast.literal_eval(txt)
    if isinstance(parsed, list):
        s_list = [str(x) for x in parsed]
    else:
        s_list = [str(parsed)]
except Exception:
    lines = [line.strip() for line in txt.splitlines() if line.strip()]
    if len(lines) == 1:
        s_list = [lines[0]]
    else:
        s_list = lines

out_lines = []
for s in s_list:
    n = len(s)
    ans = 0
    for L in range(n-1, 0, -1):
        seen = set()
        found = False
        for i in range(0, n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                ans = L
                found = True
                break
            seen.add(sub)
        if found:
            break
    out_lines.append(str(ans))

sys.stdout.write("\n".join(out_lines))

